<?php

namespace App\NoticeBoardBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * NoticeBoardsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class NoticeBoardsRepository extends EntityRepository
{
    public function findAllOrderedByName()
    {
        return $this->getEntityManager()
            ->createQuery(
                'SELECT p.title FROM NoticeBoardBundle:NoticeBoards p'
            )
            ->getResult();
    }

    public function getArrayofBoards()
    {
        return $this->getEntityManager()
            ->createQuery(
                'SELECT p.title FROM NoticeBoardBundle:NoticeBoards p'
            )
            ->getResult();
    }
}
